export function GoogleMap() {  
  const fetcher = (...args) => fetch(...args).then(res => res.json());  

  const { markers } = useContext(MapMarkersContext);
  const [address, setAddress] = React.useState("554 76th street bay ridge"); 
  var coordinates_url = `https://maps.googleapis.com/maps/api/geocode/json?key=AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo&address=${address}`;
  //var coordinates_url = `https://maps.googleapis.com/maps/api/geocode/json?key=AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo&address=554%2076th%20street%20bay%20ridge`;
  const { coordinates, error } = useSwr(coordinates_url, fetcher);
  if (error) {
    console.error('Error loading the Google Maps lng lat ', error);
  }else if (!coordinates){
    console.log("still loading");
  }
  else{
    console.log("coordinates",coordinates);
  }  

  const [newAddress, setNewAddress] = useState("");
  const [center, setCenter] = useState({lat: 40.635,lng: -73.94});

  // useEffect(() => {
  //   coordinates = {lat: coordinates.results[0].geometry.location.lat, lng: coordinates.results[0].geometry.location.lng};
  //   return () => {
  //     setMapMarkers([]);
  //   }
  // }, [coordinates, setMapMarkers]);

  return (
    <div className="google-map-container">      
      {/* <button onClick={() => setCenter({lat: 40.742,lng: -73.769})} >Change Center</button> */}
      {/* <input value={newAddress} onChange={setNewAddress}></input>
      <button onClick={() => setCenter({lat: 40.742,lng: -73.769})}>Change Center</button> */}
      <button name ="address" value = "change" onChange={changeCenter(address)}></button>
      <GoogleMapReact
        bootstrapURLKeys={{ key: "AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo" }}
        //bootstrapURLKeys={{ key: process.env.NEXT_PUBLIC_GOOGLE_MAPS_BOOTSTRAP_URL }}
        center={center}
        defaultZoom={13}
        options={getMapOptions}
      >
       {markers.map((item) => (
          <MapMarker
            key={item.key}
            //text={item.name}            
            lat={ item.coordinates.lat}
            lng={ item.coordinates.lng}
            output_key = {item.output_key}            
            name = {item.name}
            // coordinates = {item.coordinates}      
            site_info= {item.site_info}             
          />
        ))}
      </GoogleMapReact>      
    </div>
  );
}
