import React, { useEffect, useState } from 'react';
import { useRouter } from 'next/router'
import { MapMarkersContext } from 'components/MapMarkersContext';
import useSwr from 'swr';

const fetcher = (...args) => fetch(...args).then(res => res.json());

const Post = () => {
  const router = useRouter()
  const { pid, name } = router.query
  const { data, error } = useSwr('/api/testing-sites', fetcher);
  const [testing_site, setTestingSite] = useState("");  

  if (error) {
    console.error('Error loading data from API for /api/testing-sites: ', error);
  }    

  useEffect(() => {
    const formattedData = (data || []).map((site) => ({
      key: `${site.site_name}+${site.coordinates.lat}+${site.coordinates.lng}`,
      output_key: `${site.site_name}+${site.coordinates.lat}+${site.coordinates.lng}`,
      name: site.site_name,   
      site_info: {
        name: site.site_name, 
        provider_url: site.provider_url,
        additional_info: site.additional_info,
        address: site.address,
        phone: site.phone,
        monday: site.monday,
        tuesday: site.tuesday,
        wednesday: site.wednesday,
        thursday: site.thursday,
        friday: site.friday,
        saturday: site.saturday,
        sunday: site.sunday,
        screening_required: site.screening_required,
        appointment_required: site.appointment_required,
        antibody_testing: site.antibody_testing, 
      },                   
      coordinates: {
        lat: site.coordinates.lat,
        lng: site.coordinates.lng,
      },
    }));
    console.log("formattedData",formattedData);
    setTestingSite(formattedData.filter(item=>item.name===name));
  }, [data]);
  console.log("data",testing_site.site_info)

  return <div>
    {
      <p>                
        Address: {testing_site.site_info.address} <br></br> 
        {testing_site.site_info.phone ? `Phone Number: ${testing_site.site_info.phone}` : ''}{testing_site.site_info.phone ? <br></br> : ''}
        You are {testing_site.site_info.appointment_required ? 'required' : 'not required'} to have an appointment to get tested.<br></br>
        This testing site {testing_site.site_info.antibody_testing ? 'provides' : 'does not provide'} antibody testing. <br></br>        
        Hours: <br></br>
        {testing_site.site_info.sunday ? `Sunday: ${testing_site.site_info.sunday}` : ''}{testing_site.site_info.sunday ? <br></br> : ''}
        {testing_site.site_info.monday ? `Monday: ${testing_site.site_info.monday}` : ''}{testing_site.site_info.monday ? <br></br> : ''}
        {testing_site.site_info.tuesday ? `Tuesday: ${testing_site.site_info.tuesday}` : ''}{testing_site.site_info.tuesday ? <br></br> : ''}
        {testing_site.site_info.wednesday ? `Wednesday: ${testing_site.site_info.wednesday}` : ''}{testing_site.site_info.wednesday ? <br></br> : ''}
        {testing_site.site_info.thursday ? `Thursday: ${testing_site.site_info.thursday}` : ''}{testing_site.site_info.thursday ? <br></br> : ''}
        {testing_site.site_info.friday ? `Friday: ${testing_site.site_info.friday}` : ''}{testing_site.site_info.friday ? <br></br> : ''}
        {testing_site.site_info.saturday ? `Saturday: ${testing_site.site_info.saturday}` : ''}{testing_site.site_info.saturday ? <br></br> : ''}
        {testing_site.site_info.additional_info ? `Additional Information: ${testing_site.site_info.additional_info}` : ''}{testing_site.site_info.additional_info ? <br></br> : ''}         
        {testing_site.site_info.provider_url ? `URL: ` : ''}{testing_site.site_info.provider_url ? <a href = {testing_site.site_info.provider_url}>{testing_site.site_info.provider_url}</a> : ''}{testing_site.site_info.provider_url ? <br></br> : ''} 
        <Link href="/covid-testing-sites/[site]" as={`/covid-testing-sites/${testing_site.site_info.site_name}?name=${testing_site.site_info.site_name}`}>
            <a>Link to this Info</a>
        </Link>
      </p>

    }
  </div>
}

export default Post
