import React, { useContext, useState } from 'react';
import GoogleMapReact from 'google-map-react';
import useSwr from 'swr';

import { MapMarker } from 'components/MapMarker';
import { MapMarkersContext } from 'components/MapMarkersContext';

import config from './mapConfig';

const fetcher = (...args) => fetch(...args).then(res => res.json())

function getMapOptions() {
  return config;
}

function getCenter() {
  if(coordinates === undefined){
    var address = "554 76th street bay ridge";
    const { coordinates, error } = useSwr(`https://maps.googleapis.com/maps/api/geocode/json?key=AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo&address=${address}`, fetcher);
    if (error) {
      console.error('Error loading the Google Maps lng lat ', error);
    }
    return {lat: coordinates.results[0].geometry.location.lat, lng: coordinates.results[0].geometry.location.lng};    
  }else
    return {lat: 40.635,lng: -73.94};
}

function changeCenter() {  
  var coordinates = {lat: 40.742,lng: -73.769};
  return coordinates;  
}

