  //     fetch(`https://maps.googleapis.com/maps/api/geocode/json?key=AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo&address=${this.state.address}`,{
  //         method: "GET",
  //         dataType: "JSON",
  //         // headers: {
  //         //   "Content-Type": "application/json; charset=utf-8",
  //         // }
  //     })
  //     .then(response => response.json())
  //     .then(data => {           
  //       this.setState({ coordinates: {lat: data.results[0].geometry.location.lat, lng: data.results[0].geometry.location.lng}});          
  //     });
  // }
// }

export function GoogleMap() {  
  const { markers } = useContext(MapMarkersContext);
  const [address, setState] = React.useState("554 76th street bay ridge"); 
  const { coordinates, error } = useSwr(`https://maps.googleapis.com/maps/api/geocode/json?key=AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo&address=${address}`, fetcher);
  if (error) {
    console.error('Error loading the Google Maps lng lat ', error);
  }  

  useEffect(() => {
    coordinates = {lat: coordinates.results[0].geometry.location.lat, lng: coordinates.results[0].geometry.location.lng};
    return () => {
      setMapMarkers([]);
    }
  }, [coordinates, setMapMarkers]);

  return (
    <div className="google-map-container">      
      <button name ="address" value = "change" onChange={changeCenter}></button>
      <GoogleMapReact
        bootstrapURLKeys={{ key: "AIzaSyBe7_ta_1zNod6CsCJI6ssWk64kyO14HZo" }}
        //bootstrapURLKeys={{ key: process.env.NEXT_PUBLIC_GOOGLE_MAPS_BOOTSTRAP_URL }}
        center={getCenter(coordinates)}
        defaultZoom={13}
        options={getMapOptions}
      >
       {markers.map((item) => (
          <MapMarker
            key={item.key}
            //text={item.name}            
            lat={ item.coordinates.lat}
            lng={ item.coordinates.lng}
            site_info= {item.site_info}             
          />
        ))}
      </GoogleMapReact>      
    </div>
  );
}
